<?php//include_once "secure.php";session_start();include_once "../configure.php";include_once(DIR_WS_CLASS . "database.php");include_once(DIR_WS_CLASS . "db_admin_parameters.php");$msgid = intval(102);if ($_SERVER['REQUEST_METHOD'] === 'POST' && trim($_POST['login']) !== ''):    $objDB  = new database();    $link   = $objDB->db_connect();    $alogin = mysql_real_escape_string($_POST["login"], $link);    $db_password   = mysql_real_escape_string($_POST["password"], $link);    //$db_password = md5($pass);	$obj = new db_admin_parameters();    $obj->set_admin_login($alogin);    $obj->set_admin_password($db_password);    $userid = $obj->selectAll("admin_login='" . $alogin . "' and admin_password='" . $db_password . "' and active = 'Y'");    if ($userid[0] > 0):        $objId = mysql_fetch_object($userid[1]);        $_SESSION['AdminSession_ID'] = $_SESSION['Auserid']    = $objId->admin_id; //change that variable to whatever it is in your script        $_SESSION['AdminEmail']      = $objId->admin_email; //if required        $_SESSION['AdminSession_NAME'] = $_SESSION['Ausername'] = $objId->admin_login;        header("Location: adminhome.php");        exit();    endif;         $url = htmlspecialchars("index.php?msgid=".$msgid, ENT_QUOTES, 'UTF-8');         header("Location: $url");        exit();endif;